	/**
     * @param   self[]  $postTables
     */
    public function eagerLoad(array &$postTables): void
    {
        // Get the unique ticket IDs
        $ticketIDs = array_map(
            function (PostTable $x) {
                return $x->ticket_id;
            },
            $postTables
        );
        $ticketIDs = array_unique($ticketIDs);

        // Get the raw ticket data, keyed by ticket ID
        $db = $this->getDbo();
        $query = $db->getQuery(true)
            ->select('*')
            ->from('#__example_tickets')
            ->whereIn($db->quoteName('id'), $ticketIDs);
        $tickets = $db->setQuery($query)->loadObjectList('id') ?: [];

        // Convert the raw ticket data into TicketTable objects
        $tickets = array_map(
            function (object $rawData): TicketTable
            {
                $ticket = new TicketTable($this->getDbo(), $this->getDispatcher());
                $ticket->bind($rawData);

                return $ticket;
            },
            $tickets
        );

        // Set the TicketTable objects to each PostTable object
        foreach ($postTables as $postTable)
        {
            $postTable->setTicket($tickets[$postTable->ticket_id]);
        }
    }

class ItemTable extends \Joomla\CMS\Table\Table
{
    public function bind($src, $ignore = [])
    {
        $src           = (array) $src;
        $src['params'] = @json_decode($src['params']);

        return parent::bind($src, $ignore);
    }

    public function store($updateNulls = false)
    {
        $this->params = @json_decode($this->params ?? '{}', true) ?? [];

        $result = parent::store($updateNulls);

        $this->params = @json_encode($this->params) ?? '{}';

        return $result;
    }

    public function reset()
    {
        parent::reset();

        $this->params = [];
    }
}

$container->set(
  ComponentInterface::class,
  function (Container $container) {
    $component = new ExampleComponent(
      $container->get(ComponentDispatcherFactoryInterface::class)
    );

    // ... other initialisation goes here ...

    // Inject the router factory object
    $component->setRouterFactory(
      $container->get(
         \Joomla\CMS\Component\Router\RouterFactoryInterface::class
      )
    );
   }
);

public function __construct(SiteApplication $app = null, AbstractMenu $menu = null)
{
  $welcome = new \Joomla\CMS\Component\Router\RouterViewConfiguration('welcome');
  $this->registerView($welcome);

  $item = (new \Joomla\CMS\Component\Router\RouterViewConfiguration('item'))
    ->setKey('id')
    ->addLayout('default')
    ->addLayout('fancy');
  $this->registerView($item);

  $detail = (new \Joomla\CMS\Component\Router\RouterViewConfiguration('detail'))
    ->setKey('id')
    ->setParent($item, 'itemid');
  $this->registerView($detail);

  parent::__construct($app, $menu);

  $this->attachRule(new \Joomla\CMS\Component\Router\Rules\MenuRules($this));
  $this->attachRule(new \Joomla\CMS\Component\Router\Rules\StandardRules($this));
  $this->attachRule(new \Joomla\CMS\Component\Router\Rules\NomenuRules($this));
}

public function getItemId(string $segment, array $query): bool|int
{
  $db = \Joomla\CMS\Factory::getContainer()->get('DatabaseDriver');
  $dbQuery = $db->getQuery(true)
    ->select($db->quoteName('id'))
    ->from($db->quoteName('#__example_items'))
    ->where($db->quoteName('alias') . ' = :alias')
    ->bind(':alias', $segment);

  return  $db->setQuery($dbQuery)->loadResult() ?: false;
}

public function getItemSegment(int $id, array $query): array
{
  $db = \Joomla\CMS\Factory::getContainer()->get('DatabaseDriver');
  $dbQuery = $db->getQuery(true)
    ->select($db->quoteName('alias'))
    ->from($db->quoteName('#__example_items'))
    ->where($db->quoteName('id') . ' = :id')
    ->bind(':id', $id);

  $segment = $db->setQuery($dbQuery)->loadResult() ?: null;

  if ($segment === null) {
    return [];
  }

  return [$segment];
}

